mapper.select:
SELECT u.id, u.name, u.code, u.user_group_id, ug.name user_group_name, u.area_id, a.area_name, u.organization_id, o.organization_name, u.channel_id, c.name channel_name, j.job, j.duties, j.leader, j.age, j.sex, j.address, u.longitude, u.latitude, u.altitude, u.create_time FROM user u LEFT JOIN user_group ug on ug.id = u.user_group_id LEFT JOIN area a on a.id = u.area_id LEFT JOIN organization o on o.id = u.organization_id LEFT JOIN channel c on c.id = u.channel_id LEFT JOIN user_job j on u.job_id = j.id where 1=1 AND u.id = #{id} AND u.name LIKE '${name}%' AND u.code LIKE '${code}%' AND u.user_group_id = #{userGroupId} AND u.channel_id = #{channelId}
AND FIND_IN_SET(a.id, getAreaCList('${areaId}'))
AND FIND_IN_SET(o.id, getOrgCList('${organizationId}'))
ORDER BY ${sortName} ${sortOrder}
ORDER BY u.create_time DESC

mapper.update:
UPDATE user SET job_id = #{userJobId}, name=#{name},
code= #{code},user_group_id=#{userGroupId},
channel_id =#{channelId}, area_id=#{areaId},
organization_id=#{organizationId}
,longitude =#{longitude}
,latitude= #{latitude}
,altitude= #{altitude}
WHERE id = #{id}

mapper.delete:
DELETE FROM user_job WHERE user_id IN (

    ${item}

)
